<a data-toggle="modal" data-backdrop="static" data-keyboard="false" data-id="{{ $certificate->id }}" data-token="{{isset($certificate->token['token']) ? $certificate->token['token'] : ''}}" data-name="{{ $certificate->name_of_policy_holder }}" data-policy-number="{{ $certificate->policy_number }}" data-certificate-number="{{ $certificate->certificate_number }}" data-index-mark="{{ $certificate->index_mark }}" data-plate-number="{{ $certificate->plate_number }}" data-chassis-number="{{ $certificate->chassis_number }}" data-make-of-vehicle="{{ $certificate->make_of_vehicle }}" data-type-of-cover="{{ $certificate->type_of_cover }}" data-registration="{{ $certificate->registration_date }}" data-expiring="{{ $certificate->expiring_date }}" data-policy-name="{{ $certificate->policy_name }}" data-amount="{{ $certificate->amount }}" data-target="#viewCertificateModal" type="button" href="#"><i class="float-left btn btn-primary zmdi zmdi-eye"></i></a>
